var tests = []testString{
	testString{
		given:  "",
		wanted: true,
	},
}

// Function testPalindrome tests the isPalindrome function with the given range tests
func TestPalindrome(t *testing.T) {
	for _, e := range tests {
		out := IsPalindrome(e.given)
		if out != e.wanted {
			t.Fatalf(`isPalinDrome(%s) = %t, wanted %t`, e.given, out, e.wanted)
		}
	}
}
